import React from 'react';
import {connect} from "react-redux";

const Game = props => {
        return (
            <div>
                <div>
                    <input value={props.name1} onChange={e => props.setName1(e.target.value)} />
                </div>
                <div>
                    <input value={props.name2} />
                </div>
            </div>
        );
};

const mapStateToProps = (state) => ({
    name1: state.name1,
    name2: state.name2
});

const mapDispatchToProps = (dispatch) => ({
    setName1: () => (dispatch) => ({
        type: 'ADD_NAME_1'
    })
});

export default connect(mapStateToProps, mapDispatchToProps)(Game);
