const initialState = [];

export const setWinningSquares = (squares) => ({
    type: 'SET_WINNING_SQUARES',
    squares
});

const winningSquaresReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'SET_WINNING_SQUARES':
            const newState = [...state];
            newState.fill(action.squares);
            return newState;
        default:
            return state;
    }
};

export default winningSquaresReducer;
