const initialState = [
    ['', '', ''],
    ['', '', ''],
    ['', '', '']
];

export const actionPlace = () => ({
   type: 'PLACE'
});

const boardReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'PLACE':
            const newBoardState = [...state];
            const flat = newBoardState.reduce((total, amount) => {
                return total.concat(amount);
            }, []);
            console.log(flat);
            return newBoardState;
        default:
            return state;
        }
};

export default boardReducer;
