import React from 'react';
import {connect} from "react-redux";
import {handleSquareClicked} from "../thunks/handleSquareClicked";

const BoardSquare = ({handleSquareClicked, index, square, gameWon, winningSquares}) => {
    console.log(winningSquares.includes(index));
    return (
        <button
            className='square'
            onClick={() => handleSquareClicked(index)}
            disabled={gameWon}
            style={ winningSquares.includes(index) && {color: 'green'} }
        >
            {square}
        </button>
    );
};

const mapStateToProps = (state) => ({
    gameWon: state.gameWon
});

export default connect(mapStateToProps, { handleSquareClicked })(BoardSquare);
