const initialState = [
    ['', '', ''],
    ['', '', ''],
    ['', '', '']
];

export const actionPlace = (row, col) => ({
   type: 'PLACE',
   row,
   col
});

const boardReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'PLACE':
            const newBoardState = [...state];
            newBoardState.map( row => (
                console.log(row)
            ));
            console.log(newBoardState);
            return newBoardState;
        default:
            return state;
        }
};

export default boardReducer;
