import React, { Fragment } from 'react';
import BoardSquare from "./BoardSquare";
import {connect} from "react-redux";
import {setWinningSquares} from "../ducks/winningSquaresReducer";
import {gameFinished} from "../ducks/gameWonReducer";

const Board = ({currentPlayer, gameWon, board, winningSquares, name1, name2}) => {



    const name = currentPlayer === 'X'
        ? name1 : name2;
    const winningPlayer = gameWon ? currentPlayer === 'X' ?
        name2 : name1 : null;
    const message = gameWon ? `${winningPlayer} WINS!` :
        `${name}'s turn to play`;

    return (
        <Fragment>
            <div className='board'>
                {board.map( (square, index) => (
                    <BoardSquare
                        key={index}
                        square={square}
                        index={index}
                        gameFinished={gameWon}
                        winningSquares={winningSquares}
                    />
                ) )}
            </div>
            <div className='instructions'>
                <p>{message}</p>
            </div>
        </Fragment>
    );
};

const mapStateToProps = (state) => ({
    board: state.board,
    currentPlayer: state.currentPlayer,
    name1: state.players[0],
    name2: state.players[1],
    winningSquares: state.winningSquares,
    gameFinished: state.gameFinished
});

export default connect(mapStateToProps, { setWinningSquares, gameFinished })(Board);
