import React from 'react';
import {connect} from "react-redux";
import {handleSquareClicked} from "../thunks/handleSquareClicked";

const BoardSquare = ({handleSquareClicked, index, square, gameFinished, gameWon}) => {
    console.log(gameWon);
    return (
        <button
            className='square'
            onClick={() => handleSquareClicked(index)}
            disabled={gameFinished}
        >
            {square}
        </button>
    );
};

const mapStateToProps = (state) => ({
    gameFinished: state.gameWon
});

export default connect(mapStateToProps, { handleSquareClicked })(BoardSquare);
