import React from 'react';
import {connect} from "react-redux";
import {actionPlace} from "../ducks/boardReducer";
import {changePlayer} from "../ducks/currentPlayerReducer";

const BoardSquare = props => {
    const handleClick = () => {
        props.actionPlace(props.index, props.currentPlayer);
        props.changePlayer();
    };

    return (
        <button
            className='square'
            onClick={props.square == null && handleClick}
        >
            {props.square}
        </button>
    );
};

//
// const mapStateToProps = (state) => ({
//     currentPlayer: state.currentPlayer
// });

export default connect(null, { actionPlace, changePlayer })(BoardSquare);
