import React, {Component} from 'react';
import './App.css';
import {connect} from "react-redux";
import { actionSetName } from './ducks/playersReducer'
import { actionPlace } from './ducks/boardReducer'
import InputNames from "./components/inputNames";
import Board from "./components/Board";

class App extends Component {
    state = {
      inputDisabled: false
    };

    handleInputDisabled = () => {
        this.setState({
            inputDisabled: !this.state.inputDisabled
        })
    };

    render() {
        return (
            <div className="App">
                <InputNames
                    disabled={this.state.inputDisabled}
                    handleDisabled={this.handleInputDisabled}
                    name1={this.props.name1}
                    name2={this.props.name2}
                    actionSetName={this.props.actionSetName}
                />
                <button>START</button>
                { this.props.gameReady &&
                    <Board show={this.state.inputDisabled} />
                }
            </div>
        )
    }
};

const mapStateToProps = (state) => ({
    name1: state.players[0],
    name2: state.players[1],
    gameReady: state.gameReady
});

export default connect(mapStateToProps, { actionSetName, actionPlace })(App);
