import React from 'react';
import './App.css';
import {connect} from "react-redux";
import {setName1Action, setName2Action} from "./actions/actions";

const App = props => {
 return (
     <div className="App">
      <div>
       <label>Player 1: </label>
       <input type="text" value={props.name1.name} onChange={(e) => props.setName1Action(e.target.value)} />
      </div>
      <div>
       <label>Player 2: </label>
       <input type="text" value={props.name2.name} onChange={(e) => props.setName2Action(e.target.value)}/>
      </div>

      <div style={{ marginTop: 30 }}>
       <p>P1 name: {props.name1.name}</p>
       <p>P2 name: {props.name2.name}</p>
      </div>
     </div>
 )
};

const mapStateToProps = (state) => ({
    name1: state.setName1Reducer,
    name2: state.setName2Reducer
});

export default connect(mapStateToProps, { setName1Action, setName2Action })(App);
