const initialState = {
  players: ['', '']
};

const playersReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_NAME_1':
      return {
        ...state,
        players: [action.name, ...state.names[1]]
      };
    case 'SET_NAME_2':
      return {
        ...state,
        players: [...state.names[0], action.name]
      };
    default:
      return state;
  }
};

export default playersReducer;
