import React from 'react';
import {connect} from "react-redux";
import {actionPlace} from "../ducks/boardReducer";
import {changePlayer} from "../ducks/currentPlayerReducer";
import {handleSquareClicked} from "../thunks/handleSquareClicked";

const BoardSquare = ({handleSquareClicked, index, square, gameWon}) => {
    return (
        <button
            className='square'
            onClick={() => handleSquareClicked(index)}
            disabled={gameWon}
        >
            {square}
        </button>
    );
};

export default connect((state) => ({gameWon: state.winningSquares[0] !== null}), { handleSquareClicked })(BoardSquare);
