import React, { Fragment } from 'react';
import BoardSquare from "./BoardSquare";
import {connect} from "react-redux";
import {actionSetName} from "../ducks/playersReducer";

const Board = props => {
    console.log(props.name1);
        return (
            <Fragment>
                <p>{props.name1}'s turn</p>
                <div className='board'>
                    {props.board.map( (square, index) => (
                        <BoardSquare
                            key={index}
                            square={square}
                            index={index}
                        />
                    ) )}
                </div>
            </Fragment>
        );
};

const mapStateToProps = (state) => ({
    board: state.board,
    name1: state.players[0],
    name2: state.players[1]
});

export default connect(mapStateToProps, { actionSetName })(Board);
