import React from 'react';
import './App.css';
import {connect} from "react-redux";
import { actionSetName } from './ducks/playersReducer'
import { actionPlace } from './ducks/boardReducer'
import Game from "./components/Game";

const App = props => {
 return (
     <div className="App">
        <div>
            <input
                type="text"
                value={props.name1}
                onChange={(e) => props.actionSetName(1, e.target.value)}
            />
        </div>
         <div>
             <input
                 type="text"
                 value={props.name2}
                 onChange={(e) => props.actionSetName(2, e.target.value)}
             />
         </div>
         <Game />
     </div>
 )
};

const mapStateToProps = (state) => ({
    name1: state.players[0],
    name2: state.players[1]
});

export default connect(mapStateToProps, { actionSetName, actionPlace })(App);
