import React, { Fragment } from 'react';
import BoardSquare from "./BoardSquare";
import {connect} from "react-redux";
import {setWinningSquares} from "../ducks/winningSquaresReducer";

const Board = props => {

    const checkForSuccess = () => {
        if(gameWon) {
            const [a, b, c] = gameWon;
            props.setWinningSquares(a, b, c);
        }
    };

    const gameWon = wonGame(props.board);
    const name = props.currentPlayer === 'X'
        ? props.name1 : props.name2;
    const winningPlayer = gameWon ? props.currentPlayer === 'X' ?
        props.name2 : props.name1 : null;
    const message = gameWon ? `${winningPlayer} WINS!` :
        `${name}'s turn to play`;


    return (
        <Fragment>
            <div className='board'>
                {props.board.map( (square, index) => (
                    <BoardSquare
                        key={index}
                        square={square}
                        index={index}
                        currentPlayer={props.currentPlayer}
                        disabled={gameWon}
                        check={checkForSuccess}
                    />
                ) )}
            </div>
            <div className='instructions'>
                <p>{message}</p>
            </div>
        </Fragment>
    );
};

const wonGame = (squares) => {
    let won = false;
    const winningCombos = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
    ];
    winningCombos.forEach( combo => {
        const [a, b, c] = combo;
        if(squares[a] !== null && squares[a] === squares[b] && squares[b] === squares[c]) {
            won = combo;
        }
    });
    return won;
};

const mapStateToProps = (state) => ({
    board: state.board,
    currentPlayer: state.currentPlayer,
    name1: state.players[0],
    name2: state.players[1],
    winningSquares: state.winningSquares
});

export default connect(mapStateToProps, { setWinningSquares })(Board);
