const initialState = ['X', null, null, null, null, null, null, null, null];

export const actionPlace = (index, playerTurn) => ({
   type: 'PLACE',
   index,
   playerTurn
});

const boardReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'PLACE':
            const newBoardState = [...state];
            newBoardState.map((val, index) => {
                return index === action.index ? newBoardState[index] = action.playerTurn : null;
            });
            // console.log(newBoardState);
            return newBoardState;
        default:
            return state;
        }
};

export default boardReducer;
