import React from 'react';
import {connect} from "react-redux";
import {generateSetName1ActionWithName, generateSetName2ActionWithName} from "../reducers/reducer";

const Game = props => {
        return (
            <div>
                <div>
                    <input value={props.name1} onChange={e => props.dispatchSetName1ActionWithName(e.target.value)} />
                </div>
                <div>
                    <input value={props.name2} onChange={e => props.dispatchSetName2ActionWithName(e.target.value)} />
                </div>
                <button onClick={props.clearNames}>Clear</button>
            </div>
        );
};

const mapStateToProps = (state) => ({
    name1: state.name1,
    name2: state.name2
});

const mapDispatchToProps = (dispatch) => ({
    dispatchSetName1ActionWithName: (name) => (dispatch(generateSetName1ActionWithName(name))),
    dispatchSetName2ActionWithName: (name) => (dispatch(generateSetName2ActionWithName(name))),
    clearNames: () => (dispatch({
        type: "CLEAR_NAMES"
    })),
});

export default connect(mapStateToProps, mapDispatchToProps)(Game);
