import React from 'react';
import {connect} from "react-redux";
import {actionPlace} from "../ducks/boardReducer";

const BoardSquare = props => {
    const onClick = (index, player) => {
        props.actionPlace(index, player);

    };

    return (
        <button
            className='square'
            onClick={() => onClick(props.index, 'X')}
        >
            {props.square}
        </button>
    );
};


const mapStateToProps = (state) => ({
    currentPlayer: state.currentPlayer
});

export default connect(mapStateToProps, { actionPlace })(BoardSquare);
