import React from 'react';
import {connect} from "react-redux";

const setName1 = name => ({
    type: "ADD_NAME_1",
    name
});

const setName2 = name => ({
    type: "ADD_NAME_2",
    name
});

const Game = props => {
        return (
            <div>
                <div>
                    <input value={props.name1} onChange={e => props.setName1(e.target.value)} />
                </div>
                <div>
                    <input value={props.name2} onChange={e => props.setName2(e.target.value)} />
                </div>
            </div>
        );
};

const mapStateToProps = (state) => ({
    name1: state.name1,
    name2: state.name2
});

const mapDispatchToProps = (dispatch) => ({
    setName1: () => (dispatch(setName1())),
    setName2: () => (dispatch(setName2()))
});

export default connect(mapStateToProps, mapDispatchToProps)(Game);
