const initialState = {
  names: ['', '']
};

const playerReducer = (state = [initialState], action) => {
  switch (action.type) {
    case 'SET_NAME_1':
      return {
        ...state,
        names: [action.name, ...state.names[1]]
      };
    case 'SET_NAME_2':
      return {
        ...state,
        names: [...state.names[0], action.name]
      };
    default:
      return state;
  }
};

export default playerReducer;
