import React, { Fragment } from 'react';
import BoardSquare from "./BoardSquare";
import {connect} from "react-redux";
import {actionSetName} from "../ducks/playersReducer";

const Board = props => {
        const name = props.currentPlayer === 'X' ? props.name1 : props.name2;
        return (
            <Fragment>
                <div className='board'>
                    {props.board.map( (square, index) => (
                        <BoardSquare
                            key={index}
                            square={square}
                            index={index}
                        />
                    ) )}
                </div>
                <div className='instructions'>
                    { name }
                </div>
            </Fragment>
        );
};

const mapStateToProps = (state) => ({
    board: state.board,
    currentPlayer: state.currentPlayer,
    name1: state.players[0],
    name2: state.players[1]
});

export default connect(mapStateToProps, { actionSetName })(Board);
