export const changePlayer = () => ({
  type: 'CHANGE_PLAYER'
});

export const getCurrentPlayer = () => ({
    type: 'CURRENT_PLAYER'
});

const currentPlayerReducer = (state = 'X', action) => {
    switch (action.type) {
        case 'CHANGE_PLAYER':
            return state === 'X' ? 'O' : 'X';
        case 'CURRENT_PLAYER':
            return state;
        default:
            return state;
    }
};

export default currentPlayerReducer;
