import React from 'react';
import './App.css';
import {connect} from "react-redux";
import {setName1Action, setName2Action} from "./actions/actions";

const App = props => {
    console.log(props.name1);
    console.log(props.name2);
 return (
     <div className="App">
      <div>
       <label>Player 1: </label>
       <input type="text" value={props.name1.name} onChange={(e) => props.generateName1(e.target.value)} />
      </div>
      <div>
       <label>Player 2: </label>
       <input type="text" value={props.name2.name} onChange={(e) => props.generateName2(e.target.value)}/>
      </div>

      <div style={{ marginTop: 30 }}>
       <p>P1 name: {props.name1.name}</p>
       <p>P2 name: {props.name2.name}</p>
      </div>
     </div>
 )
};

const mapStateToProps = (state) => ({
    name1: state.setName1Reducer,
    name2: state.setName2Reducer
});

const mapDispatchToProps = (dispatch) => ({
    generateName1: (name) => dispatch(setName1Action(name)),
    generateName2: (name) => dispatch(setName2Action(name))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
