import React, { Fragment } from 'react';
import BoardSquare from "./BoardSquare";
import {connect} from "react-redux";

const Board = props => {
        const name = props.currentPlayer === 'X'
            ? props.name1 : props.name2;

        calculateWinner(props.board);

        return (
            <Fragment>
                <div className='board'>
                    {props.board.map( (square, index) => (
                        <BoardSquare
                            key={index}
                            square={square}
                            index={index}
                            currentPlayer={props.currentPlayer}
                        />
                    ) )}
                </div>
                <div className='instructions'>
                    <p>{ name }'s turn to play</p>
                </div>
            </Fragment>
        );
};

const calculateWinner = (squares) => {
    const winningCombos = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
    ];
    console.log(squares);
    for (let i = 0; i < winningCombos; i++) {
        const [a, b, c] = winningCombos;
    }
};

const mapStateToProps = (state) => ({
    board: state.board,
    currentPlayer: state.currentPlayer,
    name1: state.players[0],
    name2: state.players[1]
});

export default connect(mapStateToProps)(Board);
