const initialState = [
    ['', '', ''],
    ['', '', ''],
    ['', '', '']
];

export const actionPlace = () => ({
   type: 'PLACE'
});

const boardReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'PLACE':
            const newBoardState = [...state];
            console.log(newBoardState, 'ggg');
            return newBoardState
        default:
            return state;
        }
};

export default boardReducer;
