const initialState = ['', '', '', '', '', '', '', '', ''];

export const actionPlace = (row, col, playerTurn) => ({
   type: 'PLACE',
   row,
   col
});

const boardReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'PLACE':
            const newBoardState = [...state];
            newBoardState[0] = 4;
            console.log(newBoardState);
            return newBoardState;
        default:
            return state;
        }
};

export default boardReducer;
